import React from 'react';
import { Col, ListGroup, Row } from 'react-bootstrap';

function CartScreen({ items }) {
    return (
        <Row>
            <Col md={3}></Col>
            <Col md={6}>
                <ListGroup variant='flush'>
                    <ListGroup.Item>
                        <h3>CART</h3>
                    </ListGroup.Item>
                    {items.map((product, idx) => {
                        return (
                            <>
                                <Row>
                                    <Col>
                                        <ListGroup.Item>
                                            Price: ${product.price}
                                        </ListGroup.Item>
                                    </Col>
                                    <Col>
                                        <ListGroup.Item>
                                            Description: {product.name}
                                        </ListGroup.Item>
                                    </Col>
                                </Row>
                            </>
                        );
                    })}
                </ListGroup>
            </Col>
            <Col md={3}></Col>
        </Row>
    );
}

export default CartScreen;
